import React, { useState } from 'react';
import '../stylesheets/contact.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { library } from '@fortawesome/fontawesome-svg-core'
import { fab } from '@fortawesome/free-brands-svg-icons'
library.add(fab)

const Contact = props => {
  const [hide, setHide] = useState('');
  const clickHandler = ev =>{
    if(hide) {
      setHide('close');
    }
    console.log(hide);
    ev.preventDefault();
  }

    return <div className="App">
    <nav className="circular-menu">
    <div className={`circle ${hide !== '' ? 'open' : ''}`}>
      <input type="checkbox" href="#" className="menu-open" name="menu-open" id="menu-open"/>
        <a href="" className="menu-item" value="1"> <FontAwesomeIcon icon={['fab', 'linkedin']}/></a>
        <a href="" className="menu-item" value="2"> <FontAwesomeIcon icon={['fab', 'github']}/> </a>
        <a href="" className="menu-item" value="3"> <FontAwesomeIcon icon={['fab', 'twitter']}/></a>
    </div>
    <a href="/" className="menu-button fa fa-bars fa-2x" onClick={clickHandler}><FontAwesomeIcon icon={['fab', 'twitter']}/></a>
    </nav>
    </div>;
}


export default Contact;
