import React from 'react';
import '../stylesheets/contact.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { library } from '@fortawesome/fontawesome-svg-core'
import { fab } from '@fortawesome/free-brands-svg-icons'
library.add(fab)

class Contact extends React.Component {


  render() {


  var items = document.querySelectorAll('.circle a');

  for(var i = 0, l = items.length; i < l; i++) {
    items[i].style.left = (50 - 35*Math.cos(-0.5 * Math.PI - 2*(1/l)*i*Math.PI)).toFixed(4) + "%";
    items[i].style.top = (50 + 35*Math.sin(-0.5 * Math.PI - 2*(1/l)*i*Math.PI)).toFixed(4) + "%";
  }
  document.querySelector('.menu-button').onclick = function(e) {
    e.preventDefault(); document.querySelector('.circle').classList.toggle('open');
  }
    return <div className="App">
    <nav className="circular-menu">
    <div className="circle">
      <input type="checkbox" href="#" className="menu-open" name="menu-open" id="menu-open"/>
        <a href="" className="menu-item"> <FontAwesomeIcon icon={['fab', 'linkedin']}/></a>
        <a href="" className="menu-item"> <FontAwesomeIcon icon={['fab', 'github']}/> </a>
        <a href="" className="menu-item"> <FontAwesomeIcon icon={['fab', 'twitter']}/></a>
    </div>
    <a href="" className="menu-button fa fa-bars fa-2x"><FontAwesomeIcon icon={['fab', 'twitter']}/></a>
    </nav>
    </div>;
  }
}

export default Contact;
