import React, { useState } from 'react';
import '../stylesheets/contact.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { library } from '@fortawesome/fontawesome-svg-core'
import { fab } from '@fortawesome/free-brands-svg-icons'
library.add(fab)

const styleLeft = number => {
    const left = ((50 - 35*Math.cos(-0.5 * Math.PI - 2*(1/3)*number*Math.PI)).toFixed(4) + "%");
    const top = ((50 + 35*Math.sin(-0.5 * Math.PI - 2*(1/3)*number*Math.PI)).toFixed(4) + "%");

    return (`left ${left} arriba ${top}`)
  }


console.log(styleLeft(1));
console.log(styleTop(2));
const Contact = props => {
  const [hide, setHide] = useState('');
  const clickHandler = ev =>{
    ev.preventDefault();
    hide !== '' ? setHide('') : setHide('open');
  }
  return <div className="App">
  <nav className="circular-menu">
    <div className={`circle ${hide !== '' ? 'open' : ''}`}>
      <input type="checkbox" href="#" className="menu-open" name="menu-open" id="menu-open"/>
        <a href="https://www.linkedin.com/in/anabelenfunes/" style={`position: relative; left:${styleLeft(1)}; top:${styleTop(1)}`}  className="menu-item" value="1"> <FontAwesomeIcon icon={['fab', 'linkedin']}/></a>
        <a href="https://github.com/anapy" className="menu-item" value="2"> <FontAwesomeIcon icon={['fab', 'github']}/> </a>
        <a href="https://twitter.com/anajapy" className="menu-item" value="3"> <FontAwesomeIcon icon={['fab', 'twitter']}/></a>
    </div>
    <button className="menu-button" onClick={clickHandler}></button>
  </nav>
  </div>;
}

export default Contact;
